mapper.select:
SELECT COUNT(*) 
FROM ADMIN
WHERE EMAIL = #{email}

mapper.insert:
INSERT ALL 
	INTO SEQ_MANAGEMENT(
		SEQ, TABLE_NM, REG_DT
	) VALUES (
		SEQ.NEXTVAL, #{table_nm}, SYSDATE
	)
	INTO ADMIN (
		SEQ, EMAIL, PASSWORD, NAME, PHONE, DIVISION, REG_DT, USE_YN
	) VALUES (
		SEQ.NEXTVAL, #{email}, #{password}, #{name}, #{phone}, #{division}, SYSDATE, '0'
	)
SELECT * FROM DUAL
