mapper.insert:

	SELECT SEQ.NEXTVAL FROM DUAL

INSERT ALL 
	INTO SEQ_MANAGEMENT(
		SEQ, TABLE_NM, REG_DT
	) VALUES (
		#{seq}, #{table_nm}, SYSDATE
	)
	INTO QUESTION (
		SEQ, TITLE, DETAIL, ADMIN_SEQ, REG_DT
	) VALUES (
		#{seq}, #{title}, #{detail}, '${@com.galaxy.util.HttpLoginUtil@getSeq()}', SYSDATE
	)
SELECT * FROM DUAL

mapper.update:
UPDATE QUESTION SET 
	TITLE = #{title},
	DETAIL = #{detail}
WHERE SEQ = #{seq}
